/*3. Write a program that takes a year as input and outputs the Year is a Leap Year or not
Hint =>
a. The LeapYear program only works for year >= 1582, corresponding to a year in the
 Gregorian calendar.
b. Also Leap year is divisible by 4 and not divisible by 100 or divisible by 400
c. Write a method to check for Leap Year using the conditions a and b*/using System;internal class CheckLeapYear{    public static void runMethod()    {        Console.WriteLine("Enter a year:");        int year = Convert.ToInt32(Console.ReadLine());        if (IsLeapYear(year))        {            Console.WriteLine($"{year} is a Leap Year.");        }        else        {            Console.WriteLine($"{year} is not a Leap Year.");        }    }    private static bool IsLeapYear(int year)    {        if (year < 1582)        {            throw new ArgumentException("Year must be 1582 or later.");        }        return (year % 4 == 0 && year % 100 != 0) || (year % 400 == 0);    }}