/*Create a program to find the youngest friends among 3 Amar, Akbar, and Anthony based on
their ages and the tallest among the friends based on their heights
Hint =>
a. Take user input for age and height for the 3 friends and store it in two arrays each to
store the values for age and height of the 3 friends
b. Loop through the array and find the youngest of the 3 friends and the tallest of the 3
friends
c. Finally display the youngest and tallest of the 3 friends */using System;internal static class FindYoungestFriend{    public static void runMethod()    {        const int Count = 3;        string[] friendNames = { "Amar", "Akbar", "Anthony" };        int[] ages = new int[Count];        double[] heights = new double[Count];        // Input loop        for (int i = 0; i < Count; i++)        {            while (true)            {                Console.Write($"Enter age for {friendNames[i]}: ");                if (int.TryParse(Console.ReadLine(), out int age) && age > 0)                {                    ages[i] = age;                    break;                }                else                {                    Console.WriteLine("Invalid . Enter a valid age.");                }            }            while (true)            {                Console.Write($"Enter height for {friendNames[i]}: ");                if (double.TryParse(Console.ReadLine(), out double height) && height > 0)                {                    heights[i] = height;                    break;                }                else                {                    Console.WriteLine("Invalid input. Enter a valid height.");                }            }        }        // Finding youngest and tallest        int youngest = 0;        int tallest= 0;        for (int i = 1; i < Count; i++)        {            if (ages[i] < ages[youngest])            {                youngest = i;            }            if (heights[i] > heights[tallest])            {                tallest= i;            }        }                Console.WriteLine($"\nThe youngest friend is {friendNames[youngest]} with age {ages[youngest]} years.");        Console.WriteLine($"The tallest friend is {friendNames[tallest]} with height {heights[tallest]} cm.");    }}